List<IslamicStory> getAllStories() {
  return [...seeratStories, ...sahabaStories, ...prophetsStories];
}

List<IslamicStory> getStoriesByCategory(String categoryId) {
  return getAllStories().where((s) => s.category == categoryId).toList();
}
